/** * TODO */package zenghao.com.study.scaleview;import android.content.Context;/** * @author yuchuan 2014-8-18 *  */public final class ContextProvider {	private static Context sContext = null;	/**	 * NOTE(qingxia): This function should be invoked in Application while the	 * application is been created.	 * 	 * @param context	 */	public static void init(Context context) {		if (context == null) {			throw new NullPointerException(					"Can not use null initlialized application context");		}		sContext = context;	}	/**	 * Get application context.	 * 	 * @return	 */	public static Context getApplicationContext() {		if (sContext == null) {			throw new NullPointerException("Global application uninitialized");		}		return sContext;	}	private ContextProvider() {	}}